mapper.select:
SELECT ZG.FD_ID AS id, ZG.FD_NUMBER AS number, ZG.FD_PARENT AS parent, ZG.FD_TITLE AS title, ZG.FD_CUSTOMER_A AS customerKeyA, ZCM_A.FD_NAME AS customer_a_Name, ZG.FD_CUSTOMER_B AS customerKeyB, ZCM_B.FD_NAME AS customer_b_Name, ZG.FD_STATUS AS statusKey, SC. NAME AS statusName, ZG.FD_MONEY_INIT AS money_init, ZG.FD_MONEY AS money, ZG.FD_PAID AS paid, ZG.FD_UNPAID AS unpaid, ZG.FD_EXPENSES AS expenses, ZG.FD_TAX AS tax, ZG.FD_TAX_LIMIT AS taxlimit, ZG.FD_INCOME AS income, ZG.FD_CURRENCY AS currency, DATE_FORMAT( ZG.FD_SIGN_TIME, '%Y-%m-%d' ) AS signTime, DATE_FORMAT( ZG.FD_EXPIRE_TIME, '%Y-%m-%d' ) AS expireTime, ZG.CREATE_USER AS createUserId, CREATE_USER.NICK_NAME AS createUserName, ZG.LAST_CREATE_USER AS lastCreateUserId, LAST_CREATE_USER.NICK_NAME AS lastCreateUserName, DATE_FORMAT( ZG.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( ZG.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastCreateTime, ZG.FD_STATUS AS STATUS, ZG.FD_REMARK1 AS reamrks1, ZG.FD_DELETE AS del FROM ZDEV_CONTRACT_MAIN ZG LEFT JOIN system_config SC ON SC.KEY_WORD = ZG.FD_STATUS AND SC.TYPE = 'CONTRACT' LEFT JOIN ZDEV_CUSTOMER_MAIN ZCM_A ON ZCM_A.FD_ID = ZG.FD_CUSTOMER_A LEFT JOIN ZDEV_CUSTOMER_MAIN ZCM_B ON ZCM_B.FD_ID = ZG.FD_CUSTOMER_B LEFT JOIN system_administrator CREATE_USER ON CREATE_USER.ID = ZG.CREATE_USER LEFT JOIN system_administrator LAST_CREATE_USER ON LAST_CREATE_USER.ID = ZG.LAST_CREATE_USER LEFT JOIN ZDEV_CONTRACT_PARTNER ZP ON ZP.FD_ID = ZG.FD_ID LEFT JOIN ZDEV_CUSTOMER_MAIN ZM ON ZM.FD_ID = ZP.FD_USER WHERE 1=1
AND ZM.FD_USER = #{username}
AND ZG.FD_ID = #{id}
AND ZG.FD_NUMBER = #{number}
AND ZG.FD_STATUS = #{statusKey}
AND ZG.FD_PARENT = #{parent}
AND ZG.FD_TYPE = #{types}
AND ZG.FD_CUSTOMER_A = #{term} OR ZG.FD_CUSTOMER_B = #{term}
AND ZG.FD_DELETE != 'Y' GROUP BY ZG.FD_ID ORDER BY ZG.FD_STATUS , ZG.FD_SIGN_TIME DESC
,#{sort}

mapper.update:
UPDATE ZDEV_CONTRACT_MAIN ZG


        ZG.FD_DELETE = #{isValid},


WHERE ZG.FD_ID = #{id}

mapper.select:
SELECT ZC.FD_ID AS id, ZC.FD_NO AS NO, ZC.FD_TYPE AS typePayes, SC. NAME AS costType, ZC.FD_AMOUNT AS amount, ZC.FD_PAYEE AS payee, ZC.FD_PAYER AS payer, ZC.FD_REMARKS AS remarks1, ZC.CREATE_USER AS createUser, ZC.LAST_CREATE_USER AS lastCreateUser, DATE_FORMAT( ZC.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( ZC.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastCreateTime FROM ZDEV_CONTRACT_EXPENSES ZC LEFT JOIN system_config SC ON SC.KEY_WORD = ZC.FD_COSTTYPE AND SC.TYPE = 'COSTTYPE' WHERE 1=1 AND ZC.FD_ID = #{id}
AND ZC.FD_NO = #{no}
ORDER BY ZC.CREATE_TIME ASC

SELECT ZC.FD_ID AS id, ZC.FD_NO AS no, ZC.FD_USER AS user, ZCM.FD_NAME AS name, ZCN.FD_NUMBER AS num, ZCN.FD_INCOME AS mainincome, SC. NAME AS cust, ZCN.FD_TITLE AS title, ZC.FD_INCOME AS income, ZC.FD_PROPORTIONS AS pro, DATE_FORMAT( ZCN.FD_SIGN_TIME, '%Y-%m-%d' ) AS signTime, DATE_FORMAT( ZCN.FD_EXPIRE_TIME, '%Y-%m-%d' ) AS expireTime, ZCN.FD_MONEY_INIT AS money_init, ZC.FD_REMARKS AS remarks1, SCS. NAME AS status FROM ZDEV_CONTRACT_PARTNER ZC LEFT JOIN ZDEV_CUSTOMER_MAIN ZCM ON ZCM.FD_ID = ZC.FD_USER LEFT JOIN ZDEV_CONTRACT_MAIN ZCN ON ZCN.FD_ID = ZC.FD_ID LEFT JOIN system_config SC ON SC.KEY_WORD = ZC.FD_CUSTOMER AND SC.TYPE = 'TYPEC' LEFT JOIN system_config SCS ON SCS.KEY_WORD = ZCN.FD_STATUS AND SCS.TYPE = 'CONTRACT' LEFT JOIN ZDEV_CUSTOMER_MAIN ZN ON ZN.FD_ID = ZC.FD_USER WHERE 1=1 AND ZC.FD_ID = #{id}
AND ZN.FD_USER = #{username}
ORDER BY ZC.CREATE_TIME ASC

SELECT * FROM ZDEV_CONTRACT_RELATION ZC WHERE 1=1
AND ZC.FD_SUB_ID = #{subId}
AND ZC.FD_ID = #{parentId}
