mapper.select:
SELECT ZCM.FD_ID AS customerId, ZCM.FD_CODE AS code, ZCM.FD_USER AS customerUser, ZCM.FD_NAME AS customerName, ZCM.FD_SEX AS sex, ZCM.FD_TYPE AS typeKey, SC.NAME AS typeName, ZCM.FD_ID_CARD AS idCard, ZCM.FD_BANK AS bank, ZCM.FD_BANK_NO AS bankNo, ZCM.FD_ADDRESS AS address, ZCM.CREATE_USER AS createUserId, CREATE_USER.NICK_NAME AS createUserName, ZCM.LAST_CREATE_USER AS lastCreateUserId, LAST_CREATE_USER.NICK_NAME AS lastCreateUserName, DATE_FORMAT(ZCM.CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS createTime, DATE_FORMAT(ZCM.LAST_CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS lastCreateTime, ZCM.IS_VALID AS isValid, ZCM.FD_REMARKS AS remarks FROM ZDEV_CUSTOMER_MAIN ZCM LEFT JOIN system_config SC ON SC.KEY_WORD = ZCM.FD_TYPE AND SC.TYPE = 'CUSTOMER' LEFT JOIN system_administrator CREATE_USER ON CREATE_USER.ID = ZCM.CREATE_USER LEFT JOIN system_administrator LAST_CREATE_USER ON LAST_CREATE_USER.ID = ZCM.LAST_CREATE_USER WHERE 1=1
AND ZCM.FD_ID = #{customerId}
AND ZCM.FD_NAME = #{customerName}
AND ZCM.FD_TYPE = #{typeKey}
AND ZCM.FD_CODE = #{code}
AND ZCM.FD_SEX = #{sex}
ORDER BY ZCM.IS_VALID ASC
,#{sort}

mapper.update:
UPDATE ZDEV_CUSTOMER_MAIN ZCM


        ZCM.IS_VALID = #{isValid},


WHERE ZCM.FD_ID = #{id}
