mapper.select:
SELECT ZG.FD_ID AS goodsId, ZG.FD_CODE AS code, ZG.FD_NAME AS name, ZG.FD_ALIAS AS alias, ZG.FD_TYPE AS typeKey, SC. NAME AS typeName, ZG.FD_UNIT AS unitKey, SCS. NAME AS unitName, ZG.FD_PRICE AS price, ZG.CREATE_USER AS createUserId, CREATE_USER.NICK_NAME AS createUserName, ZG.LAST_CREATE_USER AS lastCreateUserId, LAST_CREATE_USER.NICK_NAME AS lastCreateUserName, DATE_FORMAT( ZG.CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS createTime, DATE_FORMAT( ZG.LAST_CREATE_TIME, '%Y-%m-%d %H:%i:%S' ) AS lastCreateTime, ZG.FD_STATUS AS status, ZG.FD_REMARKS AS remarks FROM ZDEV_GOODS_MAIN ZG LEFT JOIN system_config SC ON SC.KEY_WORD = ZG.FD_TYPE AND SC.TYPE = 'GOODS' LEFT JOIN system_config SCS ON SCS.KEY_WORD = ZG.FD_UNIT AND SCS.TYPE = 'UNIT' LEFT JOIN system_administrator CREATE_USER ON CREATE_USER.ID = ZG.CREATE_USER LEFT JOIN system_administrator LAST_CREATE_USER ON LAST_CREATE_USER.ID = ZG.LAST_CREATE_USER WHERE 1=1
AND ZG.FD_ID = #{goodsId}
AND ZG.FD_NAME = #{name}
AND ZG.FD_TYPE = #{typeKey}
ORDER BY ZG.FD_STATUS ASC
,#{sort}

mapper.update:
UPDATE ZDEV_GOODS_MAIN ZG


        ZG.FD_STATUS = #{isValid},


WHERE ZG.FD_ID = #{id}
