mapper.select:
SELECT sa.ID AS id, sa.NICK_NAME AS nickName, sa.HEAD_PORTRAIT AS headPortrait, sa.USER_NAME AS userName, sa.PHONE AS phone, sa.EMAIL AS email, sa.WEI_XIN AS weiXin, sa.QQ AS qq, sa.NAME AS name, sa.ID_CARD AS idCard, sa.STATUS AS status, sc.NAME AS statusName, DATE_FORMAT(sa.CREATE_TIME,'%Y-%m-%d %H:%i:%S') AS createTime, DATE_FORMAT(sa.UPDATE_TIME,'%Y-%m-%d %H:%i:%S') AS updateTime, sa.IS_VALID AS isValid FROM system_administrator sa LEFT JOIN system_config sc ON sc.KEY_WORD = sa. STATUS LEFT JOIN system_token st on sa.ID = st.ADMINISTRATOR_ID WHERE 1=1
AND sa.ID = #{id}
AND sa.USER_NAME LIKE CONCAT('%', #{userName}, '%')
AND sa.NICK_NAME LIKE CONCAT('%', #{nickName}, '%')
AND sa.PHONE LIKE CONCAT('%', #{phone}, '%')
AND sa.STATUS = #{status}
AND st.TOKEN = #{token}
ORDER BY sa.IS_VALID ASC
,#{sort}

mapper.update:
UPDATE system_administrator sa


        sa.IS_VALID = #{isValid},


WHERE sa.ID = #{id}
